import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import './App.css';

// Register the components
ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const App = () => {
  const [data, setData] = useState([]);
  const [round, setRound] = useState(0);
  const [score, setScore] = useState(0);
  const [result, setResult] = useState('');

  const chartRef = useRef(null);

  useEffect(() => {
    fetchData();
  }, [round]);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/get_data');
      setData(response.data.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const makePrediction = async (prediction) => {
    try {
      const response = await axios.post('http://127.0.0.1:5000/predict', { prediction });
      const machine_prediction = response.data.machine_prediction;

      let message = `Machine predicted: ${machine_prediction}`;
      if (prediction === machine_prediction) {
        setScore(score + 1);
        message += ' - You were correct!';
      } else {
        message += ' - You were wrong!';
      }

      setResult(message);
      setRound(round + 1);
    } catch (error) {
      console.error('Error making prediction:', error);
    }
  };

  const chartData = {
    labels: Array.from({ length: data.length }, (_, i) => i + 1),
    datasets: [
      {
        label: 'Index Price',
        data: data,
        borderColor: 'rgba(75, 192, 192, 1)',
        borderWidth: 1,
      },
    ],
  };

  return (
    <div className="App">
      <h1>Index Prediction Game</h1>
      <div>
        {data.length > 0 ? (
          <Line data={chartData} ref={chartRef} />
        ) : (
          <p>Loading chart...</p>
        )}
      </div>
      <div>
        <button onClick={() => makePrediction('up')}>Up</button>
        <button onClick={() => makePrediction('down')}>Down</button>
      </div>
      <p>{result}</p>
      <p>Score: {score}</p>
      {round >= 5 && <p>Game Over!</p>}
    </div>
  );
};

export default App;
