import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';
import { Line } from 'react-chartjs-2';
import './App.css';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  Title,
  Tooltip,
  Legend
);

const App = () => {
  const [data, setData] = useState([]);
  const [intervalData, setIntervalData] = useState([]);
  const [round, setRound] = useState(0);
  const [score, setScore] = useState(0);
  const [result, setResult] = useState('');
  const [username, setUsername] = useState('');
  const [isRegistered, setIsRegistered] = useState(false);
  const [leaderboard, setLeaderboard] = useState([]);
  const [showDescription, setShowDescription] = useState(false);
  const [realNextPrice, setRealNextPrice] = useState(null);
  const [machinePrediction, setMachinePrediction] = useState('');

  const chartRef = useRef(null);

  const flaskUrl = 'https://b9c1-3-66-113-240.ngrok-free.app/invoke_sagemaker';

  useEffect(() => {
    fetchData();
  }, [round]);

  useEffect(() => {
    if (data.length > 0) {
      selectRandomInterval();
    }
  }, [data]);

  useEffect(() => {
    if (isRegistered) {
      fetchLeaderboard();
    }
  }, [isRegistered]);

  const fetchLeaderboard = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/get_leaderboard');
      setLeaderboard(response.data);
    } catch (error) {
      console.error('Error fetching leaderboard:', error);
    }
  };

  const registerUser = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://127.0.0.1:5000/register', { username });
      if (response.status === 200) {
        setIsRegistered(true);
      }
    } catch (error) {
      console.error('Error registering user:', error);
    }
  };

  const fetchData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:5000/get_data');
      const formattedData = response.data.data.map(item => ({
        timestamp: item["Datetime"],
        price: item["Close"]
      }));
      // console.log(formattedData);
      setData(formattedData);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const selectRandomInterval = () => {
    const intervalLength = 30;
    const maxStart = data.length - intervalLength;
    const startIndex = Math.floor(Math.random() * maxStart);

    const selectedInterval = data.slice(startIndex, startIndex + intervalLength);
    setIntervalData(selectedInterval);
  };

  const makePrediction = async (prediction) => {
    if (round >= 5) return;

    const lastTimestamp = intervalData[intervalData.length - 1].timestamp;

    try {
      const response = await axios.post('https://b9c1-3-66-113-240.ngrok-free.app/invoke_sagemaker', { timestamps: lastTimestamp });
      const machine_prediction = response.data.machine_prediction;
      setMachinePrediction(machine_prediction);

      let message = `Machine predicted: ${machine_prediction}`;
      if (prediction === machine_prediction) {
        setScore(score + 1);
        message += ' - You were correct!';
      } else {
        message += ' - You were wrong!';
      }

      setResult(message);
      setRound(round + 1);

      if (round === 4) {
        await axios.post('http://127.0.0.1:5000/update_score', { username, score: score + 1 });
        fetchLeaderboard();
      }
    } catch (error) {
      console.error('Error making prediction:', error);
    }
  };  

  const resetGame = () => {
    setRound(0);
    setScore(0);
    setResult('');
    setIntervalData([]);
    fetchData(); 
  };

  const chartData = {
    labels: Array.from({ length: intervalData.length }, (_, i) => i + 1),
    datasets: [
      {
        label: 'Index Price',
        data: intervalData.map(item => item.price),
        borderColor: 'rgba(10, 10, 255, 1)',
        borderWidth: 1,
      },
    ],
  };

  const toggleDescription = () => {
    setShowDescription(!showDescription);
  };

  return (
    <div className="App">
      <h1 style={{marginBottom:"0px", fontSize: "80px"}}>Index Prediction Game</h1>
      <button className="question-mark" onClick={toggleDescription}>?</button>
      {showDescription && (
        <div className="description">
          <p>Welcome to the Index Prediction Game! In this game, you will see a graph showing the S&P 500 index prices for a randomly selected 30-minute interval. Your task is to predict whether the price will go up or down in the next minute. You have 5 tries to make your predictions. Your score will be updated based on the accuracy of your predictions. Good luck and have fun!</p>
        </div>
      )}
      {!isRegistered ? (
        <form onSubmit={registerUser}>
          <input
            type="text"
            placeholder="Enter username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
          <button type="submit">Register</button>
        </form>
      ) : (
        <>
        <div>
        <h3>This is an S&P 500 index price graph</h3>
        <h3>Guess where the price will go</h3>
        </div>
        <div className='game-container'>
          <div className="chart-container">
            {intervalData.length > 0 ? (
              <Line data={chartData} ref={chartRef} />
            ) : (
              <p>Loading chart...</p>
            )}
          </div>
          <div className='answer-container'>
          <p>Your score:</p>
          <h1>{score}/{round}</h1>
          <p>{result} </p>
          <div>
            <button className='button-up' onClick={() => makePrediction('up')} disabled={round >= 5}>Up</button>
            <button className='button-down' onClick={() => makePrediction('down')} disabled={round >= 5}>Down</button>
          </div>
        </div>
        </div>
        {round >= 5 && (
          <div className="overlay">
              <div className='game-over-container'>Game Over!</div>
              <p>Your final score is: {score}</p>
              <button onClick={resetGame}>Retry</button>
          </div>)
        }
         <div className="leaderboard">
            <h2 style={{fontSize:"48px"}}>Leaderboard</h2>
            <table>
              <thead>
                <tr>
                  <th>Rank</th>
                  <th>Username</th>
                  <th>Score</th>
                </tr>
              </thead>
              <tbody>
                {leaderboard.map((entry, index) => (
                  <tr key={index}>
                    <td>{index + 1}</td>
                    <td>{entry.username}</td>
                    <td>{entry.score}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </>
      )}
    </div>
  );
};

export default App;
